const exec = require('child_process').exec;

// Define the USB ports and expected device identifiers
const usbPorts = ['/dev/ttyUSB0', '/dev/ttyUSB1'];
const expectedDevices = [
    { port: '/dev/ttyUSB0', vendor: '1234', model: '5678' },
    { port: '/dev/ttyUSB1', vendor: '1234', model: '5679' }
];

const checkUsbPort = (device) => {
    exec(`udevadm info --query=all --name=${device.port}`, (error, stdout, stderr) => {
        if (error) {
            node.error(`Error checking USB port ${device.port}: ${stderr}`);
            return;
        }
        if (!stdout.includes(`ID_VENDOR_ID=${device.vendor}`) || !stdout.includes(`ID_MODEL_ID=${device.model}`)) {
            node.warn(`USB device on port ${device.port} not found or incorrect device. Restarting USB service.`);
            exec('sudo systemctl restart usb-reset.service', (error, stdout, stderr) => {
                if (error) {
                    node.error(`Error restarting USB service for port ${device.port}: ${stderr}`);
                } else {
                    node.log(`USB service restarted successfully for port ${device.port}`);
                }
            });
        } else {
            node.log(`USB device on port ${device.port} is connected and correct`);
        }
    });
};

expectedDevices.forEach(checkUsbPort);

