let resendifnoresposne = true; // resend the last message is no response is received
let resendinterval = 10; // resend last e message every x seconds
let online_threshold = 10; // Seconds between updates under which the device is considered online
let offline_threshold = 300; // Seconds between updates above which the device is considered offline

let notifmsg = null;

// Check and make sure the incoming message has a topic
if ((msg.topic==="")||(msg.topic===null)||(msg.topic===undefined)) {
    node.status({fill:"red",shape:"dot",text:"Topic missing"});
    return;
}

let lastupdate = context.get("lastupdate");
let state = context.get("state") | 0;
let queue = context.get("queue");
let queuecount = 0;
if (queue === undefined) {
    queue = [];
} else {
    if (Array.isArray(queue)) {
        queuecount = queue.length;
    } else {
        queue = [];
    }
}
let current = new Date().getTime();
let send = false;

switch (msg.topic.toLowerCase()) {
    case "update":
        // Update the timer and statistics

        if (lastupdate !== undefined) {
            notifmsg = { "topic": "Information", "payload": {} };
            current = current - lastupdate;
            current = Math.floor(current / 1000);
            notifmsg.payload.secondsincelastupdate = current;
            var minute = Math.floor(current / 60);
            var hour = Math.floor(minute / 60);
            var day = Math.floor(hour / 24);
            if (current > 24 * 60 * 60) {
                notifmsg.payload.updatetext = "Last update " + day + " days, " + hour % 24 + " hours, " + minute % 60 + " minutes, " + current % 60 + " seconds ago";
            } else if (current > 60 * 60) {
                notifmsg.payload.updatetext = "Last update " + hour % 24 + " hours, " + minute % 60 + " minutes, " + current % 60 + " seconds ago";
            } else if (current > 60) {
                notifmsg.payload.updatetext = "Last update " + minute % 60 + " minutes, " + current % 60 + " seconds ago";
            } else {
                notifmsg.payload.updatetext = "Last update " + current % 60 + " seconds ago";
            }

            // Resend last message if there is no response from the server
            if (resendifnoresposne) {
                if ((current>0) && (current % resendinterval===0)) {
                    let lastmsg = context.get("lastmsg");
                    if ((lastmsg!==undefined) && (context.get("sent"))) {
                        notifmsg.payload.resend = true;
                        if ((lastmsg.payload.fc === 1) || (lastmsg.payload.fc === 2) || (lastmsg.payload.fc === 3) || (lastmsg.payload.fc === 4)) {
                            // this is a modbus read request
                            node.status({ fill: "green", shape: "dot", text: "Read re-sent!" });
                            return [lastmsg, null, notifmsg];
                        } else {
                            // this is a modbus write request
                            node.status({ fill: "green", shape: "dot", text: "Write re-sent!" });
                            return [null, lastmsg, notifmsg];
                        }
                    }
                }
            }

            // Check for online state
            if (state !== 1) {
                if (current < online_threshold) {
                    notifmsg.topic = "Warning";
                    notifmsg.payload.text = "Device is now online";
                    notifmsg.payload.statuschange = true;
                    state = 1;
                    context.set("state", state);
                }
            } else {
                if (current > offline_threshold) {
                    notifmsg.topic = "Error";
                    notifmsg.payload.text = "Device is not transmitting";
                    notifmsg.payload.statuschange = true;
                    state = 99;
                    context.set("state", state);
                }
            }
            notifmsg.payload.state = state;
            if (state===1) {
                node.status({ fill: "blue", shape: "ring", text: queuecount+ " | "+notifmsg.payload.updatetext });
            } else {
                node.status({ fill: "red", shape: "ring", text: queuecount + " | " + notifmsg.payload.updatetext });
            }
            return [null, null, notifmsg];

        } else {
            node.status({ fill: "grey", shape: "ring", text: "No data" });
        }
        break;
    case "next":
        // Update the lastupdate counter
        context.set("lastupdate", current);
        context.set("sent", false);
        send = true;
        break;
    case "reset":
        context.set("queue",[]);
        context.set("sent", false);
        context.set("lastmsg", undefined);
        break;
    default:
        // The incoming message is a modbus request

        // delete the last msg to prevent from being resent
        // context.set("lastmsg", undefined);

        // Check if there is already a message in the queue with the same topic. 
        // If there is it needs to be deleted, because we only keep the last message.
        for (let i=queue.length-1; i>=0; i--) {
            if (queue[i].topic === msg.topic) {
                queue.splice(i,1);
            }
        }

        // Add the message to the end of the queue
        queue.push(msg);
        context.set("queue",queue);

        if (!context.get("sent")) {
            send = true;
        }
        node.status({ fill: "green", shape: "dot", text: queue.length });

}

// We need to send out a new message
if (send) {

