                node.status({ fill: 'green', shape: 'dot', text: 'Flow restarted' });
            } else {
                node.error('Failed to restart flow. Status code: ' + res.statusCode);
                node.status({ fill: 'red', shape: 'dot', text: 'Restart failed' });
            }
        });
    });

    req.on('error', (error) => {
        node.error('Failed to restart flow. Error: ' + error.message);
        node.status({ fill: 'red', shape: 'dot', text: 'Restart failed' });
    });

    req.end();

    return msg;
}

