function convertToRTU(ascii) {
    let address = Buffer.from(ascii.slice(0, 2), 'hex');
    let functionCode = Buffer.from(ascii.slice(2, 4), 'hex');
    let data = Buffer.from(ascii.slice(4), 'hex');

    let rtuBuffer = Buffer.concat([address, functionCode, data]);

    let crc = calculateCRC(rtuBuffer);

    let rtuPacket = Buffer.concat([rtuBuffer, crc]);

    return rtuPacket;
}

function calculateCRC(buffer) {
    let crc = 0xFFFF;
    for (let pos = 0; pos < buffer.length; pos++) {
        crc ^= buffer[pos];
        for (let i = 8; i !== 0; i--) {
            if ((crc & 0x0001) !== 0) {
                crc >>= 1;
                crc ^= 0xA001;
            } else {
                crc >>= 1;
            }
        }
    }
    let crcBuffer = Buffer.alloc(2);
    crcBuffer[0] = crc & 0xFF;
    crcBuffer[1] = (crc >> 8) & 0xFF;
    return crcBuffer;
}

